package lacoscondicionais;

public class If {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n1 = 4, n2 = 5, n3 = 4;
		
		if(n1 < n2) {
			n3 = 3;
			System.out.println("O n1 é menor que a soma de n2 mais n3!");
		}
		
		if (n1 > n3) {
			System.out.println("\nO n3 é maior que o n1");
		}
	}
	
}
